#include <iostream>
#include <cstdlib>

using namespace std;

string cadenaD(string num, int i, int tam)
{
    char dev[tam];
    for (int j = 0; j < tam; j++)
    {
        dev[j]= num[i];
        i++;
    }
    return dev;
}

long long Producto(int cant, int ord, string numero)
{
    char a;
    string n;
    long long produc = 0;
    for (int i = 0; i < cant+1; i++)
    {
        long long maxNum = 1;
        if((cant+1)-i>=ord)//Comprueba si aun es posible formar una cadena de 13 digitos
        {
            n = cadenaD(numero,i,ord);
            for (int j = 0; j < ord; j++)
            {
                if(n[j]!='0'){
                    a = n[j];
                    maxNum = maxNum * atoi(&a);
                }
                else
                    break;
            }
            if (maxNum>=produc)
            {
                produc = maxNum;
            }
        }
        else
            break;
    }
    return produc;
}
int main()
{
    string numero  = ("");
    cout << Producto(1000,13,numero) << endl;
}